import React, { useState } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [targetLanguage, setTargetLanguage] = useState("");
  const [translatedText, setTranslatedText] = useState("");
  const [error, setError] = useState(null);

  const handleTranslate = async () => {
    try {
      // Call the Flask API to translate text
      const response = await axios.post("http://127.0.0.1:5000/translate", {
        text: text,
        target_language: targetLanguage,
      });

      setTranslatedText(response.data.translated_text);
      setError(null);
    } catch (err) {
      setError("Error translating text. Please check the input.");
    }
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-4 text-blue-600">Language Translator</h1>

      <textarea
        placeholder="Enter text to translate"
        value={text}
        onChange={(e) => setText(e.target.value)}
        className="w-80 h-40 p-3 border-2 border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
      />

      <input
        type="text"
        placeholder="Target language code (e.g., 'en', 'es')"
        value={targetLanguage}
        onChange={(e) => setTargetLanguage(e.target.value)}
        className="w-80 p-3 border-2 border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
      />

      <button
        onClick={handleTranslate}
        className="w-80 bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition duration-300 mb-4"
      >
        Translate
      </button>

      {error && <p className="text-red-500">{error}</p>}

      {translatedText && (
        <div className="w-80 mt-6 p-4 border-2 border-gray-300 rounded-lg bg-white">
          <h2 className="text-xl font-semibold text-blue-600 mb-2">Translated Text:</h2>
          <p>{translatedText}</p>
        </div>
      )}
    </div>
  );
}

export default App;
