import React, { useState } from "react";
import axios from "axios";
import { FaGithub, FaMoon, FaSun } from "react-icons/fa";

function App() {
  const [text, setText] = useState("");
  const [targetLanguage, setTargetLanguage] = useState("");
  const [translatedText, setTranslatedText] = useState("");
  const [error, setError] = useState(null);
  const [theme, setTheme] = useState("light");

  const handleTranslate = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/translate", {
        text: text,
        target_language: targetLanguage,
      });

      setTranslatedText(response.data.translated_text);
      setError(null);
    } catch (err) {
      setError("Error translating text. Please check the input.");
    }
  };

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  return (
    <div
      className={`App min-h-screen flex flex-col justify-center items-center transition-all duration-1000 ease-in-out ${
        theme === "light"
          ? "bg-light-mode bg-cover bg-center"
          : "bg-dark-mode bg-cover bg-center"
      }`}
    >
      <div className="absolute w-full h-full bg-gradient-to-r from-[#557C93] to-[#08203E] animate-spin-slow"></div>

      <h1 className="text-white text-4xl font-bold mb-5">Language Translator</h1>

      <div className="relative w-full max-w-lg p-5 bg-white bg-opacity-80 rounded-lg shadow-lg space-y-4">
        <textarea
          className="w-full p-4 rounded-lg bg-gradient-to-r from-green-200 to-blue-200 text-black"
          placeholder="Enter text to translate"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <input
          type="text"
          placeholder="Target language code (e.g., 'en', 'es')"
          value={targetLanguage}
          onChange={(e) => setTargetLanguage(e.target.value)}
          className="w-full p-4 rounded-lg bg-gradient-to-r from-green-200 to-blue-200 text-black"
        />
        <button
          onClick={handleTranslate}
          className="w-full py-2 px-4 bg-gradient-to-r from-pink-500 to-yellow-500 text-white rounded-lg transition-all duration-300 ease-in-out hover:scale-105"
        >
          Translate
        </button>
        {error && <p className="text-red-500">{error}</p>}
        {translatedText && (
          <div className="w-full text-white text-lg font-medium overflow-hidden text-ellipsis whitespace-nowrap">
            <h2 className="text-xl font-semibold">Translated Text:</h2>
            <p>{translatedText}</p>
          </div>
        )}
      </div>

      <div className="absolute top-4 right-4 flex items-center space-x-4">
        <FaGithub size={30} color="white" />
        <a
          href="https://github.com/mansipatil"
          target="_blank"
          className="text-white font-semibold text-lg"
          rel="noopener noreferrer"
        >
          @mansipatil
        </a>
        <button onClick={toggleTheme}>
          {theme === "light" ? (
            <FaMoon size={25} color="white" />
          ) : (
            <FaSun size={25} color="white" />
          )}
        </button>
      </div>
    </div>
  );
}

export default App;
