import React, { useState } from "react";
import axios from "axios";
import './App.css'; // Import the custom CSS file for animations

function App() {
  const [text, setText] = useState("");
  const [targetLanguage, setTargetLanguage] = useState("");
  const [translatedText, setTranslatedText] = useState("");
  const [error, setError] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(false);

  const handleTranslate = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/translate", {
        text: text,
        target_language: targetLanguage,
      });

      setTranslatedText(response.data.translated_text);
      setError(null);
    } catch (err) {
      setError("Error translating text. Please check the input.");
    }
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={`min-h-screen flex flex-col justify-center items-center ${isDarkMode ? "bg-gray-900 text-white" : "bg-white text-black"} p-6 transition-all duration-500 ease-in-out relative`}>
      <div className="absolute inset-0 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 rounded-full animate-spin-slow z-0"></div>

      <div className="relative z-10 w-full text-center">
        <h1 className="text-4xl font-extrabold mb-6 transform transition-transform duration-500 hover:scale-110">
          Language Translator
        </h1>

        <textarea
          placeholder="Enter text to translate"
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="w-96 h-40 p-4 border-2 border-gray-300 rounded-lg shadow-lg focus:outline-none focus:ring-4 focus:ring-purple-500 transition-all duration-300 ease-in-out mb-6"
        />

        <input
          type="text"
          placeholder="Target language code (e.g., 'en', 'es')"
          value={targetLanguage}
          onChange={(e) => setTargetLanguage(e.target.value)}
          className="w-96 p-4 border-2 border-gray-300 rounded-lg shadow-lg focus:outline-none focus:ring-4 focus:ring-purple-500 transition-all duration-300 ease-in-out mb-6"
        />

        <button
          onClick={handleTranslate}
          className="w-96 bg-blue-600 text-white py-3 rounded-full shadow-xl hover:bg-blue-700 hover:scale-105 transition-all duration-300 ease-in-out mb-6"
        >
          Translate
        </button>

        <button
          onClick={toggleTheme}
          className="mt-4 py-2 px-6 rounded-full border-2 border-gray-300 text-lg font-semibold transition-all duration-300 ease-in-out hover:bg-gray-700 hover:text-white"
        >
          {isDarkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
        </button>

        {error && <p className="text-red-500 text-lg mb-4">{error}</p>}

        {translatedText && (
          <div className="w-96 mt-6 p-6 border-2 border-gray-300 rounded-lg bg-white shadow-xl transition-all duration-300 ease-in-out">
            <h2 className="text-xl font-semibold text-purple-600 mb-4">Translated Text:</h2>
            <p className="text-lg text-gray-700">{translatedText}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
